INSERT INTO filme (titulo, genero, ano_lancamento, sinopse, diretor_id) VALUES
                                                                            ('Indiana Jones e a Última Cruzada', 'Aventura, Ação', 1989, 'O arqueólogo Indiana Jones é recrutado por seu pai para encontrar o Santo Graal antes dos nazistas.', 1),
                                                                            ('Pulp Fiction', 'Crime, Drama', 1994, 'Uma série de histórias interligadas sobre criminosos, gangsters, boxeadores e outros personagens excêntricos em Los Angeles.', 2),
                                                                            ('A Origem', 'Ficção Científica, Ação', 2010, 'Um ladrão habilidoso, especializado em roubar segredos corporativos através da inserção de ideias no subconsciente de seus alvos, é contratado para realizar uma tarefa aparentemente impossível: plantar uma ideia na mente de um CEO.', 3),
                                                                            ('Lady Bird', 'Drama, Comédia', 2017, 'Uma adolescente em Sacramento, Califórnia, navega pelos altos e baixos de sua última temporada no ensino médio, enquanto luta contra conflitos com sua mãe e busca sua identidade.', 4),
                                                                            ('Gravidade', 'Ficção Científica, Drama', 2013, 'Durante uma missão espacial, uma astronauta e um astronauta novato ficam à deriva no espaço após o seu ônibus espacial ser destruído, lutando para sobreviver enquanto tentam encontrar um caminho de volta para casa.', 5),
                                                                            ('Tubarão', 'Suspense', 1975, 'Um grande tubarão branco aterroriza os moradores de uma pequena cidade costeira.', 1),
                                                                            ('Kill Bill: Volume 1', 'Ação', 2003, 'A Noiva, uma assassina profissional, busca vingança contra seu ex-chefe e sua equipe de assassinos depois de ser traída e deixada para morrer.', 2),
                                                                            ('Interestelar', 'Ficção Científica', 2014, 'Um grupo de exploradores espaciais viaja através de um buraco de minhoca em busca de um novo lar para a humanidade.', 3),
                                                                            ('Frances Ha', 'Comédia Dramática', 2012, 'Uma dançarina em Nova York luta para manter sua vida após o término de uma amizade de longa data.', 4),
                                                                            ('Gravidade', 'Ficção Científica', 2013, 'Durante uma missão espacial, uma astronauta e um astronauta novato ficam à deriva no espaço após o seu ônibus espacial ser destruído, lutando para sobreviver enquanto tentam encontrar um caminho de volta para casa.', 5),
                                                                            ('Jurassic Park', 'Aventura, Ficção Científica', 1993, 'Durante uma visita a um parque temático de dinossauros, um grupo de pessoas fica preso quando os dinossauros escapam de suas cercas.', 1),
                                                                            ('O Resgate do Soldado Ryan', 'Drama, Guerra', 1998, 'Durante a Segunda Guerra Mundial, um grupo de soldados é enviado atrás das linhas inimigas para resgatar um soldado cujos três irmãos foram mortos em combate.', 1);
